import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useQuery, useMutation } from 'react-query';
import {
    Star,
    Heart,
    Share2,
    Calendar,
    Clock,
    MapPin,
    User,
    Shield,
    Phone,
    MessageCircle,
    ArrowLeft,
    CheckCircle
} from 'lucide-react';
import axios from 'axios';
import toast from 'react-hot-toast';

const ServiceDetail = () => {
    const { id } = useParams();
    const [selectedDate, setSelectedDate] = useState('');
    const [selectedTime, setSelectedTime] = useState('');
    const [isBooking, setIsBooking] = useState(false);

    const { data: service, isLoading } = useQuery(
        ['service', id],
        () => axios.get(`/api/marketplace/services/${id}`).then(res => res.data),
        {
            enabled: !!id,
        }
    );

    const bookServiceMutation = useMutation(
        (data) => axios.post('/api/bookings', data),
        {
            onSuccess: () => {
                toast.success('Service booked successfully!');
            },
            onError: (error) => {
                toast.error('Failed to book service');
                console.error('Booking error:', error);
            },
            onSettled: () => {
                setIsBooking(false);
            }
        }
    );

    const handleBookService = () => {
        if (!selectedDate || !selectedTime) {
            toast.error('Please select date and time');
            return;
        }

        setIsBooking(true);
        bookServiceMutation.mutate({
            serviceId: service._id,
            date: selectedDate,
            time: selectedTime
        });
    };

    if (isLoading) {
        return (
            <div className="min-h-screen flex items-center justify-center">
                <div className="spinner"></div>
            </div>
        );
    }

    if (!service) {
        return (
            <div className="min-h-screen flex items-center justify-center">
                <div className="text-center">
                    <h2 className="text-2xl font-bold text-gray-900 mb-4">Service not found</h2>
                    <Link to="/marketplace" className="btn btn-primary">
                        Back to Marketplace
                    </Link>
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gray-50">
            {/* Breadcrumb */}
            <div className="bg-white border-b">
                <div className="container mx-auto px-4 py-4">
                    <div className="flex items-center space-x-2 text-sm text-gray-600">
                        <Link to="/marketplace" className="hover:text-blue-600">Marketplace</Link>
                        <span>/</span>
                        <Link to="/marketplace" className="hover:text-blue-600">Services</Link>
                        <span>/</span>
                        <span className="text-gray-900">{service.name}</span>
                    </div>
                </div>
            </div>

            <div className="container mx-auto px-4 py-8">
                <div className="grid lg:grid-cols-3 gap-8">
                    {/* Service Info */}
                    <div className="lg:col-span-2 space-y-6">
                        {/* Hero Section */}
                        <div className="card p-6">
                            <div className="flex items-center space-x-2 mb-4">
                                <span className="bg-blue-100 text-blue-600 px-2 py-1 rounded-full text-xs font-medium capitalize">
                                    {service.category}
                                </span>
                                {service.isVerified && (
                                    <span className="bg-green-100 text-green-600 px-2 py-1 rounded-full text-xs font-medium flex items-center">
                                        <Shield className="w-3 h-3 mr-1" />
                                        Verified
                                    </span>
                                )}
                            </div>

                            <h1 className="text-3xl font-bold text-gray-900 mb-4">{service.name}</h1>

                            <div className="flex items-center space-x-4 mb-6">
                                <div className="flex items-center space-x-1">
                                    <Star className="w-5 h-5 text-yellow-500 fill-current" />
                                    <span className="text-lg font-semibold">
                                        {service.rating?.average?.toFixed(1) || '4.5'}
                                    </span>
                                    <span className="text-gray-600">
                                        ({service.rating?.count || 0} reviews)
                                    </span>
                                </div>
                            </div>

                            <div className="flex items-center space-x-4 mb-6">
                                <span className="text-3xl font-bold text-gray-900">
                                    ₹{service.pricing?.amount}
                                </span>
                                <span className="text-lg text-gray-600">
                                    per {service.pricing?.type}
                                </span>
                            </div>

                            <p className="text-gray-600 leading-relaxed">{service.description}</p>
                        </div>

                        {/* Experience & Certifications */}
                        {service.experience && (
                            <div className="card p-6">
                                <h3 className="text-lg font-semibold text-gray-900 mb-4">Experience & Certifications</h3>
                                <div className="space-y-4">
                                    {service.experience.years && (
                                        <div className="flex items-center space-x-3">
                                            <Clock className="w-5 h-5 text-gray-400" />
                                            <span className="text-gray-700">
                                                {service.experience.years} years of experience
                                            </span>
                                        </div>
                                    )}
                                    {service.experience.description && (
                                        <p className="text-gray-600">{service.experience.description}</p>
                                    )}
                                    {service.certifications && service.certifications.length > 0 && (
                                        <div>
                                            <h4 className="font-medium text-gray-900 mb-2">Certifications:</h4>
                                            <div className="space-y-2">
                                                {service.certifications.map((cert, index) => (
                                                    <div key={index} className="flex items-center space-x-2">
                                                        <CheckCircle className="w-4 h-4 text-green-500" />
                                                        <span className="text-gray-700">
                                                            {cert.name} - {cert.issuer}
                                                        </span>
                                                    </div>
                                                ))}
                                            </div>
                                        </div>
                                    )}
                                </div>
                            </div>
                        )}

                        {/* Availability */}
                        {service.availability && (
                            <div className="card p-6">
                                <h3 className="text-lg font-semibold text-gray-900 mb-4">Availability</h3>
                                <div className="space-y-4">
                                    <div>
                                        <h4 className="font-medium text-gray-900 mb-2">Available Days:</h4>
                                        <div className="flex flex-wrap gap-2">
                                            {service.availability.days?.map((day, index) => (
                                                <span key={index} className="bg-blue-100 text-blue-600 px-3 py-1 rounded-full text-sm">
                                                    {day}
                                                </span>
                                            ))}
                                        </div>
                                    </div>
                                    {service.availability.timeSlots && service.availability.timeSlots.length > 0 && (
                                        <div>
                                            <h4 className="font-medium text-gray-900 mb-2">Time Slots:</h4>
                                            <div className="grid grid-cols-2 md:grid-cols-3 gap-2">
                                                {service.availability.timeSlots.map((slot, index) => (
                                                    <span key={index} className="bg-gray-100 text-gray-700 px-3 py-2 rounded-lg text-sm text-center">
                                                        {slot.start} - {slot.end}
                                                    </span>
                                                ))}
                                            </div>
                                        </div>
                                    )}
                                </div>
                            </div>
                        )}

                        {/* Languages */}
                        {service.languages && service.languages.length > 0 && (
                            <div className="card p-6">
                                <h3 className="text-lg font-semibold text-gray-900 mb-4">Languages</h3>
                                <div className="flex flex-wrap gap-2">
                                    {service.languages.map((language, index) => (
                                        <span key={index} className="bg-green-100 text-green-600 px-3 py-1 rounded-full text-sm">
                                            {language}
                                        </span>
                                    ))}
                                </div>
                            </div>
                        )}

                        {/* Reviews */}
                        {service.reviews && service.reviews.length > 0 && (
                            <div className="card p-6">
                                <h3 className="text-lg font-semibold text-gray-900 mb-6">Customer Reviews</h3>
                                <div className="space-y-6">
                                    {service.reviews.slice(0, 5).map((review, index) => (
                                        <div key={index} className="border-b border-gray-200 pb-6 last:border-b-0">
                                            <div className="flex items-center justify-between mb-2">
                                                <div className="flex items-center space-x-2">
                                                    <div className="w-8 h-8 bg-gray-300 rounded-full flex items-center justify-center">
                                                        <span className="text-sm font-medium text-gray-600">
                                                            {review.user?.name?.charAt(0) || 'U'}
                                                        </span>
                                                    </div>
                                                    <span className="font-medium text-gray-900">
                                                        {review.user?.name || 'Anonymous'}
                                                    </span>
                                                </div>
                                                <div className="flex items-center space-x-1">
                                                    {[...Array(5)].map((_, i) => (
                                                        <Star
                                                            key={i}
                                                            className={`w-4 h-4 ${i < review.rating ? 'text-yellow-400 fill-current' : 'text-gray-300'
                                                                }`}
                                                        />
                                                    ))}
                                                </div>
                                            </div>
                                            <p className="text-gray-600 mb-2">{review.comment}</p>
                                            <p className="text-sm text-gray-500">
                                                {new Date(review.createdAt).toLocaleDateString()}
                                            </p>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}
                    </div>

                    {/* Booking Sidebar */}
                    <div className="space-y-6">
                        {/* Booking Card */}
                        <div className="card p-6">
                            <h3 className="text-lg font-semibold text-gray-900 mb-4">Book This Service</h3>

                            <div className="space-y-4">
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Select Date
                                    </label>
                                    <input
                                        type="date"
                                        value={selectedDate}
                                        onChange={(e) => setSelectedDate(e.target.value)}
                                        min={new Date().toISOString().split('T')[0]}
                                        className="input"
                                    />
                                </div>

                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Select Time
                                    </label>
                                    <select
                                        value={selectedTime}
                                        onChange={(e) => setSelectedTime(e.target.value)}
                                        className="input"
                                    >
                                        <option value="">Choose time</option>
                                        {service.availability?.timeSlots?.map((slot, index) => (
                                            <option key={index} value={`${slot.start}-${slot.end}`}>
                                                {slot.start} - {slot.end}
                                            </option>
                                        ))}
                                    </select>
                                </div>

                                <div className="border-t border-gray-200 pt-4">
                                    <div className="flex items-center justify-between mb-4">
                                        <span className="text-gray-700">Total Price:</span>
                                        <span className="text-xl font-bold text-gray-900">
                                            ₹{service.pricing?.amount}
                                        </span>
                                    </div>

                                    <button
                                        onClick={handleBookService}
                                        disabled={isBooking || !service.availability?.isAvailable}
                                        className="w-full btn btn-primary py-3"
                                    >
                                        {isBooking ? (
                                            <div className="flex items-center justify-center">
                                                <div className="spinner mr-2"></div>
                                                Booking...
                                            </div>
                                        ) : (
                                            <>
                                                <Calendar className="w-5 h-5 mr-2" />
                                                Book Now
                                            </>
                                        )}
                                    </button>

                                    {!service.availability?.isAvailable && (
                                        <p className="text-red-600 text-sm mt-2 text-center">
                                            Service currently unavailable
                                        </p>
                                    )}
                                </div>
                            </div>
                        </div>

                        {/* Provider Info */}
                        <div className="card p-6">
                            <h3 className="text-lg font-semibold text-gray-900 mb-4">Service Provider</h3>
                            <div className="space-y-4">
                                <div className="flex items-center space-x-4">
                                    <div className="w-16 h-16 bg-gray-300 rounded-full flex items-center justify-center">
                                        <User className="w-8 h-8 text-gray-600" />
                                    </div>
                                    <div className="flex-1">
                                        <h4 className="font-semibold text-gray-900">{service.provider?.name || 'Local Provider'}</h4>
                                        <div className="flex items-center space-x-4 text-sm text-gray-600">
                                            <div className="flex items-center space-x-1">
                                                <Star className="w-4 h-4 text-yellow-500 fill-current" />
                                                <span>{service.provider?.rating || '4.5'}/5</span>
                                            </div>
                                        </div>
                                    </div>
                                </div>

                                <div className="space-y-2">
                                    <button className="w-full btn btn-outline">
                                        <MessageCircle className="w-4 h-4 mr-2" />
                                        Send Message
                                    </button>
                                    <button className="w-full btn btn-secondary">
                                        <Phone className="w-4 h-4 mr-2" />
                                        Call Provider
                                    </button>
                                </div>
                            </div>
                        </div>

                        {/* Location */}
                        {service.location && (
                            <div className="card p-6">
                                <h3 className="text-lg font-semibold text-gray-900 mb-4">Location</h3>
                                <div className="space-y-2">
                                    <div className="flex items-center space-x-2">
                                        <MapPin className="w-5 h-5 text-gray-400" />
                                        <span className="text-gray-700">{service.location.address || 'Jharkhand, India'}</span>
                                    </div>
                                    {service.location.coverage && service.location.coverage.length > 0 && (
                                        <div>
                                            <p className="text-sm text-gray-600 mb-1">Coverage Areas:</p>
                                            <div className="flex flex-wrap gap-1">
                                                {service.location.coverage.map((area, index) => (
                                                    <span key={index} className="bg-gray-100 text-gray-600 px-2 py-1 rounded text-xs">
                                                        {area}
                                                    </span>
                                                ))}
                                            </div>
                                        </div>
                                    )}
                                </div>
                            </div>
                        )}

                        {/* Actions */}
                        <div className="card p-6">
                            <div className="space-y-3">
                                <button className="w-full btn btn-secondary">
                                    <Heart className="w-4 h-4 mr-2" />
                                    Add to Favorites
                                </button>
                                <button className="w-full btn btn-secondary">
                                    <Share2 className="w-4 h-4 mr-2" />
                                    Share Service
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ServiceDetail;
