import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useMutation } from 'react-query';
import {
    Calendar,
    Users,
    DollarSign,
    MapPin,
    Clock,
    Wand2,
    Save,
    Share2
} from 'lucide-react';
import axios from 'axios';
import toast from 'react-hot-toast';

const ItineraryPlanner = () => {
    const { user } = useAuth();
    const [formData, setFormData] = useState({
        duration: 3,
        budget: 10000,
        travelers: {
            adults: 2,
            children: 0
        },
        interests: [],
        accommodation: 'mid-range',
        startDate: '',
        specialRequests: ''
    });

    const [generatedItinerary, setGeneratedItinerary] = useState(null);
    const [isGenerating, setIsGenerating] = useState(false);

    const interestOptions = [
        'Nature & Wildlife',
        'Cultural Heritage',
        'Adventure Sports',
        'Religious Sites',
        'Local Cuisine',
        'Photography',
        'Shopping',
        'Relaxation'
    ];

    const accommodationOptions = [
        { value: 'budget', label: 'Budget (₹500-1500/night)' },
        { value: 'mid-range', label: 'Mid-range (₹1500-3000/night)' },
        { value: 'luxury', label: 'Luxury (₹3000+/night)' }
    ];

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        if (name === 'adults' || name === 'children') {
            setFormData(prev => ({
                ...prev,
                travelers: {
                    ...prev.travelers,
                    [name]: parseInt(value)
                }
            }));
        } else {
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handleInterestToggle = (interest) => {
        setFormData(prev => ({
            ...prev,
            interests: prev.interests.includes(interest)
                ? prev.interests.filter(i => i !== interest)
                : [...prev.interests, interest]
        }));
    };

    const generateItineraryMutation = useMutation(
        (data) => axios.post('/api/itineraries/generate', data),
        {
            onSuccess: (response) => {
                setGeneratedItinerary(response.data);
                toast.success('Itinerary generated successfully!');
            },
            onError: (error) => {
                toast.error('Failed to generate itinerary. Please try again.');
                console.error('Itinerary generation error:', error);
            },
            onSettled: () => {
                setIsGenerating(false);
            }
        }
    );

    const handleGenerateItinerary = async (e) => {
        e.preventDefault();
        setIsGenerating(true);

        const itineraryData = {
            ...formData,
            budget: parseInt(formData.budget)
        };

        generateItineraryMutation.mutate(itineraryData);
    };

    const saveItineraryMutation = useMutation(
        (data) => axios.post('/api/itineraries', data),
        {
            onSuccess: () => {
                toast.success('Itinerary saved successfully!');
            },
            onError: (error) => {
                toast.error('Failed to save itinerary.');
                console.error('Save error:', error);
            }
        }
    );

    const handleSaveItinerary = () => {
        if (generatedItinerary) {
            saveItineraryMutation.mutate(generatedItinerary);
        }
    };

    return (
        <div className="min-h-screen bg-gray-50">
            {/* Header */}
            <div className="bg-gradient-to-r from-blue-600 to-purple-700 text-white py-16">
                <div className="container mx-auto px-4">
                    <h1 className="text-4xl font-bold mb-4">AI Trip Planner</h1>
                    <p className="text-xl text-blue-100">
                        Let our AI create the perfect itinerary for your Jharkhand adventure
                    </p>
                </div>
            </div>

            <div className="container mx-auto px-4 py-8">
                <div className="grid lg:grid-cols-2 gap-8">
                    {/* Form */}
                    <div className="card p-6">
                        <h2 className="text-2xl font-bold text-gray-900 mb-6">Plan Your Trip</h2>

                        <form onSubmit={handleGenerateItinerary} className="space-y-6">
                            {/* Duration */}
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                    Trip Duration
                                </label>
                                <div className="flex items-center space-x-2">
                                    <Calendar className="w-5 h-5 text-gray-400" />
                                    <input
                                        type="number"
                                        name="duration"
                                        value={formData.duration}
                                        onChange={handleInputChange}
                                        min="1"
                                        max="14"
                                        className="input flex-1"
                                        required
                                    />
                                    <span className="text-gray-600">days</span>
                                </div>
                            </div>

                            {/* Travelers */}
                            <div className="grid grid-cols-2 gap-4">
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Adults
                                    </label>
                                    <div className="flex items-center space-x-2">
                                        <Users className="w-5 h-5 text-gray-400" />
                                        <input
                                            type="number"
                                            name="adults"
                                            value={formData.travelers.adults}
                                            onChange={handleInputChange}
                                            min="1"
                                            max="10"
                                            className="input"
                                            required
                                        />
                                    </div>
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Children
                                    </label>
                                    <div className="flex items-center space-x-2">
                                        <Users className="w-5 h-5 text-gray-400" />
                                        <input
                                            type="number"
                                            name="children"
                                            value={formData.travelers.children}
                                            onChange={handleInputChange}
                                            min="0"
                                            max="10"
                                            className="input"
                                        />
                                    </div>
                                </div>
                            </div>

                            {/* Budget */}
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                    Budget (₹)
                                </label>
                                <div className="flex items-center space-x-2">
                                    <DollarSign className="w-5 h-5 text-gray-400" />
                                    <input
                                        type="number"
                                        name="budget"
                                        value={formData.budget}
                                        onChange={handleInputChange}
                                        min="1000"
                                        step="1000"
                                        className="input flex-1"
                                        required
                                    />
                                </div>
                            </div>

                            {/* Start Date */}
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                    Start Date
                                </label>
                                <div className="flex items-center space-x-2">
                                    <Calendar className="w-5 h-5 text-gray-400" />
                                    <input
                                        type="date"
                                        name="startDate"
                                        value={formData.startDate}
                                        onChange={handleInputChange}
                                        className="input flex-1"
                                        min={new Date().toISOString().split('T')[0]}
                                    />
                                </div>
                            </div>

                            {/* Accommodation */}
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                    Accommodation Preference
                                </label>
                                <select
                                    name="accommodation"
                                    value={formData.accommodation}
                                    onChange={handleInputChange}
                                    className="input"
                                >
                                    {accommodationOptions.map(option => (
                                        <option key={option.value} value={option.value}>
                                            {option.label}
                                        </option>
                                    ))}
                                </select>
                            </div>

                            {/* Interests */}
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                    Interests (Select all that apply)
                                </label>
                                <div className="grid grid-cols-2 gap-2">
                                    {interestOptions.map(interest => (
                                        <label key={interest} className="flex items-center space-x-2">
                                            <input
                                                type="checkbox"
                                                checked={formData.interests.includes(interest)}
                                                onChange={() => handleInterestToggle(interest)}
                                                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                                            />
                                            <span className="text-sm text-gray-700">{interest}</span>
                                        </label>
                                    ))}
                                </div>
                            </div>

                            {/* Special Requests */}
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                    Special Requests (Optional)
                                </label>
                                <textarea
                                    name="specialRequests"
                                    value={formData.specialRequests}
                                    onChange={handleInputChange}
                                    rows="3"
                                    className="input"
                                    placeholder="Any specific requirements or preferences..."
                                />
                            </div>

                            <button
                                type="submit"
                                disabled={isGenerating}
                                className="w-full btn btn-primary py-3 text-lg"
                            >
                                {isGenerating ? (
                                    <div className="flex items-center justify-center">
                                        <div className="spinner mr-2"></div>
                                        Generating Itinerary...
                                    </div>
                                ) : (
                                    <>
                                        <Wand2 className="w-5 h-5 mr-2" />
                                        Generate AI Itinerary
                                    </>
                                )}
                            </button>
                        </form>
                    </div>

                    {/* Generated Itinerary */}
                    <div className="space-y-6">
                        {generatedItinerary ? (
                            <div className="card p-6">
                                <div className="flex items-center justify-between mb-6">
                                    <h2 className="text-2xl font-bold text-gray-900">
                                        {generatedItinerary.title}
                                    </h2>
                                    <div className="flex space-x-2">
                                        <button
                                            onClick={handleSaveItinerary}
                                            className="btn btn-secondary"
                                            disabled={saveItineraryMutation.isLoading}
                                        >
                                            <Save className="w-4 h-4 mr-2" />
                                            Save
                                        </button>
                                        <button className="btn btn-outline">
                                            <Share2 className="w-4 h-4 mr-2" />
                                            Share
                                        </button>
                                    </div>
                                </div>

                                <div className="space-y-4">
                                    <div className="bg-blue-50 p-4 rounded-lg">
                                        <h3 className="font-semibold text-blue-900 mb-2">Trip Summary</h3>
                                        <div className="grid grid-cols-2 gap-4 text-sm">
                                            <div>
                                                <span className="text-blue-700">Duration:</span>
                                                <span className="ml-2 text-blue-900">{generatedItinerary.duration.days} days</span>
                                            </div>
                                            <div>
                                                <span className="text-blue-700">Budget:</span>
                                                <span className="ml-2 text-blue-900">₹{generatedItinerary.budget.min} - ₹{generatedItinerary.budget.max}</span>
                                            </div>
                                            <div>
                                                <span className="text-blue-700">Travelers:</span>
                                                <span className="ml-2 text-blue-900">
                                                    {generatedItinerary.travelers.adults} adults, {generatedItinerary.travelers.children} children
                                                </span>
                                            </div>
                                            <div>
                                                <span className="text-blue-700">Type:</span>
                                                <span className="ml-2 text-blue-900 capitalize">{generatedItinerary.preferences.accommodation}</span>
                                            </div>
                                        </div>
                                    </div>

                                    {generatedItinerary.days && generatedItinerary.days.length > 0 ? (
                                        <div className="space-y-4">
                                            <h3 className="text-lg font-semibold text-gray-900">Day-by-Day Itinerary</h3>
                                            {generatedItinerary.days.map((day, index) => (
                                                <div key={index} className="border border-gray-200 rounded-lg p-4">
                                                    <h4 className="font-semibold text-gray-900 mb-2">
                                                        Day {day.dayNumber}
                                                    </h4>
                                                    {day.activities && day.activities.length > 0 ? (
                                                        <div className="space-y-2">
                                                            {day.activities.map((activity, actIndex) => (
                                                                <div key={actIndex} className="flex items-center justify-between p-2 bg-gray-50 rounded">
                                                                    <div>
                                                                        <span className="font-medium">{activity.activity}</span>
                                                                        {activity.destination && (
                                                                            <span className="text-gray-600 ml-2">at {activity.destination.name}</span>
                                                                        )}
                                                                    </div>
                                                                    <div className="flex items-center space-x-2 text-sm text-gray-500">
                                                                        <Clock className="w-4 h-4" />
                                                                        <span>{activity.startTime} - {activity.endTime}</span>
                                                                    </div>
                                                                </div>
                                                            ))}
                                                        </div>
                                                    ) : (
                                                        <p className="text-gray-600">Activities will be planned based on your preferences.</p>
                                                    )}
                                                </div>
                                            ))}
                                        </div>
                                    ) : (
                                        <div className="text-center py-8">
                                            <p className="text-gray-600">
                                                Your personalized itinerary will be generated based on your preferences.
                                            </p>
                                        </div>
                                    )}
                                </div>
                            </div>
                        ) : (
                            <div className="card p-6 text-center">
                                <Wand2 className="w-16 h-16 text-gray-300 mx-auto mb-4" />
                                <h3 className="text-lg font-semibold text-gray-900 mb-2">
                                    Ready to Plan Your Trip?
                                </h3>
                                <p className="text-gray-600">
                                    Fill out the form to generate your personalized AI-powered itinerary for Jharkhand.
                                </p>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ItineraryPlanner;
