import React, { useState } from 'react';
import { useQuery } from 'react-query';
import { Link } from 'react-router-dom';
import { MapPin, Star, Search, Filter, Grid, List } from 'lucide-react';
import axios from 'axios';

const Destinations = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('');
    const [viewMode, setViewMode] = useState('grid');
    const [sortBy, setSortBy] = useState('rating');

    const { data: destinations, isLoading } = useQuery(
        ['destinations', searchTerm, selectedCategory, sortBy],
        () => {
            const params = new URLSearchParams();
            if (searchTerm) params.append('search', searchTerm);
            if (selectedCategory) params.append('category', selectedCategory);
            params.append('sortBy', sortBy);
            return axios.get(`/api/destinations?${params}`).then(res => res.data);
        },
        {
            staleTime: 5 * 60 * 1000,
        }
    );

    const categories = [
        { value: '', label: 'All Categories' },
        { value: 'nature', label: 'Nature' },
        { value: 'cultural', label: 'Cultural' },
        { value: 'historical', label: 'Historical' },
        { value: 'adventure', label: 'Adventure' },
        { value: 'religious', label: 'Religious' },
        { value: 'wildlife', label: 'Wildlife' },
    ];

    const sortOptions = [
        { value: 'rating', label: 'Highest Rated' },
        { value: 'name', label: 'Name A-Z' },
        { value: 'createdAt', label: 'Newest' },
    ];

    return (
        <div className="min-h-screen bg-gray-50">
            {/* Header */}
            <div className="bg-gradient-to-r from-blue-600 to-purple-700 text-white py-16">
                <div className="container mx-auto px-4">
                    <h1 className="text-4xl font-bold mb-4">Explore Jharkhand</h1>
                    <p className="text-xl text-blue-100">
                        Discover the natural beauty and rich culture of Jharkhand
                    </p>
                </div>
            </div>

            {/* Filters */}
            <div className="container mx-auto px-4 py-8">
                <div className="bg-white rounded-lg shadow-sm p-6 mb-8">
                    <div className="flex flex-col lg:flex-row gap-4">
                        {/* Search */}
                        <div className="flex-1">
                            <div className="relative">
                                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                                <input
                                    type="text"
                                    placeholder="Search destinations..."
                                    value={searchTerm}
                                    onChange={(e) => setSearchTerm(e.target.value)}
                                    className="input pl-10"
                                />
                            </div>
                        </div>

                        {/* Category Filter */}
                        <div className="lg:w-48">
                            <select
                                value={selectedCategory}
                                onChange={(e) => setSelectedCategory(e.target.value)}
                                className="input"
                            >
                                {categories.map(category => (
                                    <option key={category.value} value={category.value}>
                                        {category.label}
                                    </option>
                                ))}
                            </select>
                        </div>

                        {/* Sort */}
                        <div className="lg:w-48">
                            <select
                                value={sortBy}
                                onChange={(e) => setSortBy(e.target.value)}
                                className="input"
                            >
                                {sortOptions.map(option => (
                                    <option key={option.value} value={option.value}>
                                        {option.label}
                                    </option>
                                ))}
                            </select>
                        </div>

                        {/* View Mode */}
                        <div className="flex space-x-2">
                            <button
                                onClick={() => setViewMode('grid')}
                                className={`p-2 rounded ${viewMode === 'grid' ? 'bg-blue-100 text-blue-600' : 'text-gray-400'}`}
                            >
                                <Grid className="w-5 h-5" />
                            </button>
                            <button
                                onClick={() => setViewMode('list')}
                                className={`p-2 rounded ${viewMode === 'list' ? 'bg-blue-100 text-blue-600' : 'text-gray-400'}`}
                            >
                                <List className="w-5 h-5" />
                            </button>
                        </div>
                    </div>
                </div>

                {/* Results */}
                {isLoading ? (
                    <div className="flex justify-center py-12">
                        <div className="spinner"></div>
                    </div>
                ) : (
                    <>
                        <div className="flex items-center justify-between mb-6">
                            <p className="text-gray-600">
                                {destinations?.total || 0} destinations found
                            </p>
                        </div>

                        {viewMode === 'grid' ? (
                            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                                {destinations?.destinations?.map((destination) => (
                                    <div key={destination._id} className="card overflow-hidden">
                                        <div className="relative h-48">
                                            <img
                                                src={destination.images?.[0]?.url || "https://images.unsplash.com/photo-1506905925346-21bda4d32df4?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80"}
                                                alt={destination.name}
                                                className="w-full h-full object-cover"
                                            />
                                            <div className="absolute top-4 right-4 bg-white bg-opacity-90 rounded-full px-3 py-1 flex items-center space-x-1">
                                                <Star className="w-4 h-4 text-yellow-500 fill-current" />
                                                <span className="text-sm font-medium">
                                                    {destination.rating?.average?.toFixed(1) || '4.5'}
                                                </span>
                                            </div>
                                            <div className="absolute bottom-4 left-4">
                                                <span className="bg-blue-600 text-white px-2 py-1 rounded-full text-xs font-medium capitalize">
                                                    {destination.category}
                                                </span>
                                            </div>
                                        </div>
                                        <div className="p-6">
                                            <h3 className="text-xl font-semibold text-gray-900 mb-2">
                                                {destination.name}
                                            </h3>
                                            <p className="text-gray-600 mb-4 line-clamp-2">
                                                {destination.shortDescription || destination.description}
                                            </p>
                                            <div className="flex items-center justify-between">
                                                <div className="flex items-center text-sm text-gray-500">
                                                    <MapPin className="w-4 h-4 mr-1" />
                                                    <span>{destination.location?.district || 'Jharkhand'}</span>
                                                </div>
                                                <Link
                                                    to={`/destinations/${destination._id}`}
                                                    className="text-blue-600 hover:text-blue-800 font-medium"
                                                >
                                                    Explore →
                                                </Link>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <div className="space-y-4">
                                {destinations?.destinations?.map((destination) => (
                                    <div key={destination._id} className="card p-6">
                                        <div className="flex flex-col md:flex-row gap-6">
                                            <div className="md:w-48 h-32 md:h-24">
                                                <img
                                                    src={destination.images?.[0]?.url || "https://images.unsplash.com/photo-1506905925346-21bda4d32df4?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80"}
                                                    alt={destination.name}
                                                    className="w-full h-full object-cover rounded-lg"
                                                />
                                            </div>
                                            <div className="flex-1">
                                                <div className="flex items-start justify-between mb-2">
                                                    <h3 className="text-xl font-semibold text-gray-900">
                                                        {destination.name}
                                                    </h3>
                                                    <div className="flex items-center space-x-1">
                                                        <Star className="w-4 h-4 text-yellow-500 fill-current" />
                                                        <span className="text-sm font-medium">
                                                            {destination.rating?.average?.toFixed(1) || '4.5'}
                                                        </span>
                                                    </div>
                                                </div>
                                                <p className="text-gray-600 mb-3">
                                                    {destination.shortDescription || destination.description}
                                                </p>
                                                <div className="flex items-center justify-between">
                                                    <div className="flex items-center space-x-4 text-sm text-gray-500">
                                                        <div className="flex items-center">
                                                            <MapPin className="w-4 h-4 mr-1" />
                                                            <span>{destination.location?.district || 'Jharkhand'}</span>
                                                        </div>
                                                        <span className="bg-blue-100 text-blue-600 px-2 py-1 rounded-full text-xs font-medium capitalize">
                                                            {destination.category}
                                                        </span>
                                                    </div>
                                                    <Link
                                                        to={`/destinations/${destination._id}`}
                                                        className="btn btn-primary"
                                                    >
                                                        Explore
                                                    </Link>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        )}

                        {destinations?.destinations?.length === 0 && (
                            <div className="text-center py-12">
                                <MapPin className="w-16 h-16 text-gray-300 mx-auto mb-4" />
                                <h3 className="text-xl font-semibold text-gray-900 mb-2">
                                    No destinations found
                                </h3>
                                <p className="text-gray-600">
                                    Try adjusting your search criteria or browse all destinations.
                                </p>
                            </div>
                        )}
                    </>
                )}
            </div>
        </div>
    );
};

export default Destinations;
