import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { MessageCircle, X, Send, Bot, User } from 'lucide-react';
import axios from 'axios';
import toast from 'react-hot-toast';

const Chatbot = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [messages, setMessages] = useState([
        {
            id: 1,
            text: "Namaste! I'm your AI travel assistant for Jharkhand. How can I help you plan your perfect trip?",
            sender: 'bot',
            timestamp: new Date(),
        }
    ]);
    const [inputMessage, setInputMessage] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [selectedLanguage, setSelectedLanguage] = useState('en');
    const messagesEndRef = useRef(null);
    const { isAuthenticated } = useAuth();

    const languages = [
        { code: 'en', name: 'English' },
        { code: 'hi', name: 'हिन्दी' },
        { code: 'bn', name: 'বাংলা' },
        { code: 'or', name: 'ଓଡ଼ିଆ' },
    ];

    const quickQuestions = [
        "What are the must-visit places in Jharkhand?",
        "Best time to visit Jharkhand?",
        "How to reach Netarhat?",
        "What is the local cuisine like?",
        "Tell me about tribal culture",
    ];

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    const handleSendMessage = async (message = inputMessage) => {
        if (!message.trim()) return;

        const userMessage = {
            id: Date.now(),
            text: message,
            sender: 'user',
            timestamp: new Date(),
        };

        setMessages(prev => [...prev, userMessage]);
        setInputMessage('');
        setIsLoading(true);

        try {
            const response = await axios.post('/api/chatbot/chat', {
                message,
                context: 'general',
                language: selectedLanguage
            });

            const botMessage = {
                id: Date.now() + 1,
                text: response.data.response,
                sender: 'bot',
                timestamp: new Date(),
            };

            setMessages(prev => [...prev, botMessage]);
        } catch (error) {
            console.error('Chatbot error:', error);
            toast.error('Sorry, I encountered an error. Please try again.');

            const errorMessage = {
                id: Date.now() + 1,
                text: "I'm sorry, I'm having trouble right now. Please try again in a moment.",
                sender: 'bot',
                timestamp: new Date(),
            };

            setMessages(prev => [...prev, errorMessage]);
        } finally {
            setIsLoading(false);
        }
    };

    const handleQuickQuestion = (question) => {
        handleSendMessage(question);
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            handleSendMessage();
        }
    };

    const clearChat = () => {
        setMessages([
            {
                id: 1,
                text: "Namaste! I'm your AI travel assistant for Jharkhand. How can I help you plan your perfect trip?",
                sender: 'bot',
                timestamp: new Date(),
            }
        ]);
    };

    if (!isOpen) {
        return (
            <div className="chatbot-container">
                <button
                    onClick={() => setIsOpen(true)}
                    className="chatbot-toggle"
                    title="Chat with AI Assistant"
                >
                    <MessageCircle className="w-6 h-6" />
                </button>
            </div>
        );
    }

    return (
        <div className="chatbot-container">
            <div className="chatbot-window open">
                {/* Header */}
                <div className="bg-gradient-to-r from-blue-600 to-purple-700 text-white p-4 flex items-center justify-between">
                    <div className="flex items-center space-x-2">
                        <div className="w-8 h-8 bg-white/20 rounded-full flex items-center justify-center">
                            <Bot className="w-5 h-5" />
                        </div>
                        <div>
                            <span className="font-semibold">AI Travel Assistant</span>
                            <p className="text-xs text-blue-100">Online</p>
                        </div>
                    </div>
                    <div className="flex items-center space-x-2">
                        <select
                            value={selectedLanguage}
                            onChange={(e) => setSelectedLanguage(e.target.value)}
                            className="bg-white bg-opacity-20 text-white text-xs rounded px-2 py-1 border-none"
                        >
                            {languages.map(lang => (
                                <option key={lang.code} value={lang.code} className="text-gray-900">
                                    {lang.name}
                                </option>
                            ))}
                        </select>
                        <button
                            onClick={() => setIsOpen(false)}
                            className="text-white hover:text-gray-200 transition-colors"
                        >
                            <X className="w-5 h-5" />
                        </button>
                    </div>
                </div>

                {/* Messages */}
                <div className="flex-1 overflow-y-auto p-4 space-y-4 bg-gradient-to-b from-gray-50 to-white">
                    {messages.map((message, index) => (
                        <div
                            key={message.id}
                            className={`flex ${message.sender === 'user' ? 'justify-end' : 'justify-start'} slide-up`}
                            style={{ animationDelay: `${index * 0.1}s` }}
                        >
                            <div
                                className={`max-w-xs lg:max-w-md px-4 py-3 rounded-2xl shadow-sm ${message.sender === 'user'
                                    ? 'bg-gradient-to-r from-blue-600 to-purple-600 text-white'
                                    : 'bg-white text-gray-900 border border-gray-100'
                                    }`}
                            >
                                <div className="flex items-start space-x-2">
                                    {message.sender === 'bot' && (
                                        <Bot className="w-4 h-4 mt-1 flex-shrink-0" />
                                    )}
                                    {message.sender === 'user' && (
                                        <User className="w-4 h-4 mt-1 flex-shrink-0" />
                                    )}
                                    <div>
                                        <p className="text-sm">{message.text}</p>
                                        <p className="text-xs opacity-70 mt-1">
                                            {message.timestamp.toLocaleTimeString([], {
                                                hour: '2-digit',
                                                minute: '2-digit'
                                            })}
                                        </p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}

                    {isLoading && (
                        <div className="flex justify-start">
                            <div className="bg-gray-100 text-gray-900 max-w-xs px-4 py-2 rounded-lg">
                                <div className="flex items-center space-x-2">
                                    <Bot className="w-4 h-4" />
                                    <div className="flex space-x-1">
                                        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                                        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                                        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    )}

                    <div ref={messagesEndRef} />
                </div>

                {/* Quick Questions */}
                {messages.length === 1 && (
                    <div className="px-4 pb-2">
                        <p className="text-xs text-gray-500 mb-2">Quick questions:</p>
                        <div className="flex flex-wrap gap-1">
                            {quickQuestions.slice(0, 3).map((question, index) => (
                                <button
                                    key={index}
                                    onClick={() => handleQuickQuestion(question)}
                                    className="text-xs bg-gray-100 hover:bg-gray-200 text-gray-700 px-2 py-1 rounded-full transition-colors"
                                >
                                    {question}
                                </button>
                            ))}
                        </div>
                    </div>
                )}

                {/* Input */}
                <div className="p-4 border-t border-gray-200">
                    <div className="flex items-center space-x-2">
                        <input
                            type="text"
                            value={inputMessage}
                            onChange={(e) => setInputMessage(e.target.value)}
                            onKeyPress={handleKeyPress}
                            placeholder="Ask me anything about Jharkhand..."
                            className="flex-1 input text-sm py-2"
                            disabled={isLoading}
                        />
                        <button
                            onClick={() => handleSendMessage()}
                            disabled={isLoading || !inputMessage.trim()}
                            className="btn btn-primary p-2 disabled:opacity-50 disabled:cursor-not-allowed"
                        >
                            <Send className="w-4 h-4" />
                        </button>
                    </div>

                    <div className="flex items-center justify-between mt-2">
                        <button
                            onClick={clearChat}
                            className="text-xs text-gray-500 hover:text-gray-700 transition-colors"
                        >
                            Clear chat
                        </button>
                        {!isAuthenticated && (
                            <Link
                                to="/register"
                                className="text-xs text-blue-600 hover:text-blue-800 transition-colors"
                            >
                                Sign up for better experience
                            </Link>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Chatbot;
