import React, { useState } from 'react';
import { useQuery } from 'react-query';
import { Link } from 'react-router-dom';
import {
    ShoppingBag,
    Star,
    Search,
    Filter,
    Grid,
    List,
    MapPin,
    User,
    Clock
} from 'lucide-react';
import axios from 'axios';

const Marketplace = () => {
    const [activeTab, setActiveTab] = useState('products');
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('');
    const [viewMode, setViewMode] = useState('grid');
    const [sortBy, setSortBy] = useState('createdAt');

    const { data: products, isLoading: productsLoading } = useQuery(
        ['products', searchTerm, selectedCategory, sortBy],
        () => {
            const params = new URLSearchParams();
            if (searchTerm) params.append('search', searchTerm);
            if (selectedCategory) params.append('category', selectedCategory);
            params.append('sortBy', sortBy);
            return axios.get(`/api/marketplace/products?${params}`).then(res => res.data);
        },
        {
            enabled: activeTab === 'products',
            staleTime: 5 * 60 * 1000,
        }
    );

    const { data: services, isLoading: servicesLoading } = useQuery(
        ['services', searchTerm, selectedCategory, sortBy],
        () => {
            const params = new URLSearchParams();
            if (searchTerm) params.append('search', searchTerm);
            if (selectedCategory) params.append('category', selectedCategory);
            params.append('sortBy', sortBy);
            return axios.get(`/api/marketplace/services?${params}`).then(res => res.data);
        },
        {
            enabled: activeTab === 'services',
            staleTime: 5 * 60 * 1000,
        }
    );

    const productCategories = [
        { value: '', label: 'All Categories' },
        { value: 'handicrafts', label: 'Handicrafts' },
        { value: 'textiles', label: 'Textiles' },
        { value: 'jewelry', label: 'Jewelry' },
        { value: 'art', label: 'Art' },
        { value: 'food', label: 'Food' },
        { value: 'services', label: 'Services' },
    ];

    const serviceCategories = [
        { value: '', label: 'All Categories' },
        { value: 'guide', label: 'Tour Guides' },
        { value: 'transport', label: 'Transportation' },
        { value: 'accommodation', label: 'Accommodation' },
        { value: 'food', label: 'Food & Dining' },
        { value: 'entertainment', label: 'Entertainment' },
        { value: 'wellness', label: 'Wellness' },
    ];

    const sortOptions = [
        { value: 'createdAt', label: 'Newest' },
        { value: 'rating', label: 'Highest Rated' },
        { value: 'price', label: 'Price: Low to High' },
        { value: '-price', label: 'Price: High to Low' },
    ];

    const currentData = activeTab === 'products' ? products : services;
    const isLoading = activeTab === 'products' ? productsLoading : servicesLoading;
    const categories = activeTab === 'products' ? productCategories : serviceCategories;

    return (
        <div className="min-h-screen bg-gray-50">
            {/* Header */}
            <div className="bg-gradient-to-r from-green-600 via-blue-600 to-purple-600 text-white py-20 relative overflow-hidden">
                <div className="absolute inset-0 bg-black/20"></div>
                <div className="container mx-auto px-4 relative z-10">
                    <div className="text-center">
                        <h1 className="text-5xl font-bold mb-4 fade-in">Local Marketplace</h1>
                        <p className="text-xl text-green-100 mb-8 fade-in">
                            Discover authentic tribal handicrafts and local services
                        </p>
                        <div className="flex justify-center space-x-4 fade-in">
                            <div className="bg-white/20 backdrop-blur-sm rounded-full px-6 py-3">
                                <span className="text-sm font-medium">🎨 Handicrafts</span>
                            </div>
                            <div className="bg-white/20 backdrop-blur-sm rounded-full px-6 py-3">
                                <span className="text-sm font-medium">🏠 Homestays</span>
                            </div>
                            <div className="bg-white/20 backdrop-blur-sm rounded-full px-6 py-3">
                                <span className="text-sm font-medium">🍽️ Local Cuisine</span>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            <div className="container mx-auto px-4 py-8">
                {/* Tabs */}
                <div className="flex space-x-1 bg-gray-200 rounded-lg p-1 mb-8">
                    <button
                        onClick={() => setActiveTab('products')}
                        className={`flex-1 py-2 px-4 rounded-md font-medium transition-colors ${activeTab === 'products'
                            ? 'bg-white text-gray-900 shadow-sm'
                            : 'text-gray-600 hover:text-gray-900'
                            }`}
                    >
                        <ShoppingBag className="w-5 h-5 inline mr-2" />
                        Products
                    </button>
                    <button
                        onClick={() => setActiveTab('services')}
                        className={`flex-1 py-2 px-4 rounded-md font-medium transition-colors ${activeTab === 'services'
                            ? 'bg-white text-gray-900 shadow-sm'
                            : 'text-gray-600 hover:text-gray-900'
                            }`}
                    >
                        <User className="w-5 h-5 inline mr-2" />
                        Services
                    </button>
                </div>

                {/* Filters */}
                <div className="bg-white rounded-lg shadow-sm p-6 mb-8">
                    <div className="flex flex-col lg:flex-row gap-4">
                        {/* Search */}
                        <div className="flex-1">
                            <div className="relative">
                                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                                <input
                                    type="text"
                                    placeholder={`Search ${activeTab}...`}
                                    value={searchTerm}
                                    onChange={(e) => setSearchTerm(e.target.value)}
                                    className="input pl-10"
                                />
                            </div>
                        </div>

                        {/* Category Filter */}
                        <div className="lg:w-48">
                            <select
                                value={selectedCategory}
                                onChange={(e) => setSelectedCategory(e.target.value)}
                                className="input"
                            >
                                {categories.map(category => (
                                    <option key={category.value} value={category.value}>
                                        {category.label}
                                    </option>
                                ))}
                            </select>
                        </div>

                        {/* Sort */}
                        <div className="lg:w-48">
                            <select
                                value={sortBy}
                                onChange={(e) => setSortBy(e.target.value)}
                                className="input"
                            >
                                {sortOptions.map(option => (
                                    <option key={option.value} value={option.value}>
                                        {option.label}
                                    </option>
                                ))}
                            </select>
                        </div>

                        {/* View Mode */}
                        <div className="flex space-x-2">
                            <button
                                onClick={() => setViewMode('grid')}
                                className={`p-2 rounded ${viewMode === 'grid' ? 'bg-blue-100 text-blue-600' : 'text-gray-400'}`}
                            >
                                <Grid className="w-5 h-5" />
                            </button>
                            <button
                                onClick={() => setViewMode('list')}
                                className={`p-2 rounded ${viewMode === 'list' ? 'bg-blue-100 text-blue-600' : 'text-gray-400'}`}
                            >
                                <List className="w-5 h-5" />
                            </button>
                        </div>
                    </div>
                </div>

                {/* Results */}
                {isLoading ? (
                    <div className="flex justify-center py-12">
                        <div className="spinner"></div>
                    </div>
                ) : (
                    <>
                        <div className="flex items-center justify-between mb-6">
                            <p className="text-gray-600">
                                {currentData?.total || 0} {activeTab} found
                            </p>
                        </div>

                        {viewMode === 'grid' ? (
                            <div className="marketplace-grid">
                                {activeTab === 'products' ? (
                                    products?.products?.map((product) => (
                                        <div key={product._id} className="card overflow-hidden">
                                            <div className="relative h-48">
                                                <img
                                                    src={product.images?.[0]?.url || "https://images.unsplash.com/photo-1441986300917-64674bd600d8?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80"}
                                                    alt={product.name}
                                                    className="w-full h-full object-cover"
                                                />
                                                <div className="absolute top-4 right-4 bg-white bg-opacity-90 rounded-full px-3 py-1 flex items-center space-x-1">
                                                    <Star className="w-4 h-4 text-yellow-500 fill-current" />
                                                    <span className="text-sm font-medium">
                                                        {product.rating?.average?.toFixed(1) || '4.5'}
                                                    </span>
                                                </div>
                                                <div className="absolute bottom-4 left-4">
                                                    <span className="bg-green-600 text-white px-2 py-1 rounded-full text-xs font-medium capitalize">
                                                        {product.category}
                                                    </span>
                                                </div>
                                            </div>
                                            <div className="p-6">
                                                <h3 className="text-xl font-semibold text-gray-900 mb-2">
                                                    {product.name}
                                                </h3>
                                                <p className="text-gray-600 mb-4 line-clamp-2">
                                                    {product.description}
                                                </p>
                                                <div className="flex items-center justify-between">
                                                    <div className="flex items-center text-sm text-gray-500">
                                                        <User className="w-4 h-4 mr-1" />
                                                        <span>{product.vendor?.name || 'Local Vendor'}</span>
                                                    </div>
                                                    <div className="text-right">
                                                        <span className="text-lg font-semibold text-gray-900">
                                                            ₹{product.price}
                                                        </span>
                                                    </div>
                                                </div>
                                                <Link
                                                    to={`/marketplace/products/${product._id}`}
                                                    className="block w-full mt-4 btn btn-primary text-center"
                                                >
                                                    View Details
                                                </Link>
                                            </div>
                                        </div>
                                    ))
                                ) : (
                                    services?.services?.map((service) => (
                                        <div key={service._id} className="card overflow-hidden">
                                            <div className="relative h-48">
                                                <img
                                                    src={service.images?.[0]?.url || "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80"}
                                                    alt={service.name}
                                                    className="w-full h-full object-cover"
                                                />
                                                <div className="absolute top-4 right-4 bg-white bg-opacity-90 rounded-full px-3 py-1 flex items-center space-x-1">
                                                    <Star className="w-4 h-4 text-yellow-500 fill-current" />
                                                    <span className="text-sm font-medium">
                                                        {service.rating?.average?.toFixed(1) || '4.5'}
                                                    </span>
                                                </div>
                                                <div className="absolute bottom-4 left-4">
                                                    <span className="bg-blue-600 text-white px-2 py-1 rounded-full text-xs font-medium capitalize">
                                                        {service.category}
                                                    </span>
                                                </div>
                                            </div>
                                            <div className="p-6">
                                                <h3 className="text-xl font-semibold text-gray-900 mb-2">
                                                    {service.name}
                                                </h3>
                                                <p className="text-gray-600 mb-4 line-clamp-2">
                                                    {service.description}
                                                </p>
                                                <div className="flex items-center justify-between mb-4">
                                                    <div className="flex items-center text-sm text-gray-500">
                                                        <MapPin className="w-4 h-4 mr-1" />
                                                        <span>{service.location?.address || 'Jharkhand'}</span>
                                                    </div>
                                                    <div className="text-right">
                                                        <span className="text-lg font-semibold text-gray-900">
                                                            ₹{service.pricing?.amount}
                                                        </span>
                                                        <span className="text-sm text-gray-500 ml-1">
                                                            /{service.pricing?.type}
                                                        </span>
                                                    </div>
                                                </div>
                                                <Link
                                                    to={`/marketplace/services/${service._id}`}
                                                    className="block w-full btn btn-primary text-center"
                                                >
                                                    View Details
                                                </Link>
                                            </div>
                                        </div>
                                    ))
                                )}
                            </div>
                        ) : (
                            <div className="space-y-4">
                                {activeTab === 'products' ? (
                                    products?.products?.map((product) => (
                                        <div key={product._id} className="card p-6">
                                            <div className="flex flex-col md:flex-row gap-6">
                                                <div className="md:w-48 h-32 md:h-24">
                                                    <img
                                                        src={product.images?.[0]?.url || "https://images.unsplash.com/photo-1441986300917-64674bd600d8?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80"}
                                                        alt={product.name}
                                                        className="w-full h-full object-cover rounded-lg"
                                                    />
                                                </div>
                                                <div className="flex-1">
                                                    <div className="flex items-start justify-between mb-2">
                                                        <h3 className="text-xl font-semibold text-gray-900">
                                                            {product.name}
                                                        </h3>
                                                        <div className="flex items-center space-x-1">
                                                            <Star className="w-4 h-4 text-yellow-500 fill-current" />
                                                            <span className="text-sm font-medium">
                                                                {product.rating?.average?.toFixed(1) || '4.5'}
                                                            </span>
                                                        </div>
                                                    </div>
                                                    <p className="text-gray-600 mb-3">
                                                        {product.description}
                                                    </p>
                                                    <div className="flex items-center justify-between">
                                                        <div className="flex items-center space-x-4 text-sm text-gray-500">
                                                            <div className="flex items-center">
                                                                <User className="w-4 h-4 mr-1" />
                                                                <span>{product.vendor?.name || 'Local Vendor'}</span>
                                                            </div>
                                                            <span className="bg-green-100 text-green-600 px-2 py-1 rounded-full text-xs font-medium capitalize">
                                                                {product.category}
                                                            </span>
                                                        </div>
                                                        <div className="flex items-center space-x-4">
                                                            <span className="text-lg font-semibold text-gray-900">
                                                                ₹{product.price}
                                                            </span>
                                                            <Link
                                                                to={`/marketplace/products/${product._id}`}
                                                                className="btn btn-primary"
                                                            >
                                                                View Details
                                                            </Link>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    ))
                                ) : (
                                    services?.services?.map((service) => (
                                        <div key={service._id} className="card p-6">
                                            <div className="flex flex-col md:flex-row gap-6">
                                                <div className="md:w-48 h-32 md:h-24">
                                                    <img
                                                        src={service.images?.[0]?.url || "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80"}
                                                        alt={service.name}
                                                        className="w-full h-full object-cover rounded-lg"
                                                    />
                                                </div>
                                                <div className="flex-1">
                                                    <div className="flex items-start justify-between mb-2">
                                                        <h3 className="text-xl font-semibold text-gray-900">
                                                            {service.name}
                                                        </h3>
                                                        <div className="flex items-center space-x-1">
                                                            <Star className="w-4 h-4 text-yellow-500 fill-current" />
                                                            <span className="text-sm font-medium">
                                                                {service.rating?.average?.toFixed(1) || '4.5'}
                                                            </span>
                                                        </div>
                                                    </div>
                                                    <p className="text-gray-600 mb-3">
                                                        {service.description}
                                                    </p>
                                                    <div className="flex items-center justify-between">
                                                        <div className="flex items-center space-x-4 text-sm text-gray-500">
                                                            <div className="flex items-center">
                                                                <MapPin className="w-4 h-4 mr-1" />
                                                                <span>{service.location?.address || 'Jharkhand'}</span>
                                                            </div>
                                                            <span className="bg-blue-100 text-blue-600 px-2 py-1 rounded-full text-xs font-medium capitalize">
                                                                {service.category}
                                                            </span>
                                                        </div>
                                                        <div className="flex items-center space-x-4">
                                                            <span className="text-lg font-semibold text-gray-900">
                                                                ₹{service.pricing?.amount}
                                                                <span className="text-sm text-gray-500 ml-1">
                                                                    /{service.pricing?.type}
                                                                </span>
                                                            </span>
                                                            <Link
                                                                to={`/marketplace/services/${service._id}`}
                                                                className="btn btn-primary"
                                                            >
                                                                View Details
                                                            </Link>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    ))
                                )}
                            </div>
                        )}

                        {currentData?.[activeTab]?.length === 0 && (
                            <div className="text-center py-12">
                                <ShoppingBag className="w-16 h-16 text-gray-300 mx-auto mb-4" />
                                <h3 className="text-xl font-semibold text-gray-900 mb-2">
                                    No {activeTab} found
                                </h3>
                                <p className="text-gray-600">
                                    Try adjusting your search criteria or browse all {activeTab}.
                                </p>
                            </div>
                        )}
                    </>
                )}
            </div>
        </div>
    );
};

export default Marketplace;
