import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useQuery, useMutation } from 'react-query';
import {
    Star,
    Heart,
    Share2,
    ShoppingCart,
    Truck,
    Shield,
    User,
    MapPin,
    ArrowLeft,
    Plus,
    Minus
} from 'lucide-react';
import axios from 'axios';
import toast from 'react-hot-toast';

const ProductDetail = () => {
    const { id } = useParams();
    const [selectedImage, setSelectedImage] = useState(0);
    const [quantity, setQuantity] = useState(1);
    const [isAddingToCart, setIsAddingToCart] = useState(false);

    const { data: product, isLoading } = useQuery(
        ['product', id],
        () => axios.get(`/api/marketplace/products/${id}`).then(res => res.data),
        {
            enabled: !!id,
        }
    );

    const addToCartMutation = useMutation(
        (data) => axios.post('/api/cart/add', data),
        {
            onSuccess: () => {
                toast.success('Added to cart successfully!');
            },
            onError: (error) => {
                toast.error('Failed to add to cart');
                console.error('Add to cart error:', error);
            },
            onSettled: () => {
                setIsAddingToCart(false);
            }
        }
    );

    const handleAddToCart = () => {
        setIsAddingToCart(true);
        addToCartMutation.mutate({
            productId: product._id,
            quantity
        });
    };

    if (isLoading) {
        return (
            <div className="min-h-screen flex items-center justify-center">
                <div className="spinner"></div>
            </div>
        );
    }

    if (!product) {
        return (
            <div className="min-h-screen flex items-center justify-center">
                <div className="text-center">
                    <h2 className="text-2xl font-bold text-gray-900 mb-4">Product not found</h2>
                    <Link to="/marketplace" className="btn btn-primary">
                        Back to Marketplace
                    </Link>
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gray-50">
            {/* Breadcrumb */}
            <div className="bg-white border-b">
                <div className="container mx-auto px-4 py-4">
                    <div className="flex items-center space-x-2 text-sm text-gray-600">
                        <Link to="/marketplace" className="hover:text-blue-600">Marketplace</Link>
                        <span>/</span>
                        <Link to="/marketplace" className="hover:text-blue-600">Products</Link>
                        <span>/</span>
                        <span className="text-gray-900">{product.name}</span>
                    </div>
                </div>
            </div>

            <div className="container mx-auto px-4 py-8">
                <div className="grid lg:grid-cols-2 gap-8">
                    {/* Product Images */}
                    <div className="space-y-4">
                        <div className="aspect-square bg-white rounded-lg overflow-hidden">
                            <img
                                src={product.images?.[selectedImage]?.url || "https://images.unsplash.com/photo-1441986300917-64674bd600d8?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80"}
                                alt={product.name}
                                className="w-full h-full object-cover"
                            />
                        </div>

                        {product.images && product.images.length > 1 && (
                            <div className="grid grid-cols-4 gap-2">
                                {product.images.map((image, index) => (
                                    <button
                                        key={index}
                                        onClick={() => setSelectedImage(index)}
                                        className={`aspect-square rounded-lg overflow-hidden border-2 ${selectedImage === index ? 'border-blue-600' : 'border-gray-200'
                                            }`}
                                    >
                                        <img
                                            src={image.url}
                                            alt={`${product.name} ${index + 1}`}
                                            className="w-full h-full object-cover"
                                        />
                                    </button>
                                ))}
                            </div>
                        )}
                    </div>

                    {/* Product Info */}
                    <div className="space-y-6">
                        <div>
                            <div className="flex items-center space-x-2 mb-2">
                                <span className="bg-green-100 text-green-600 px-2 py-1 rounded-full text-xs font-medium capitalize">
                                    {product.category}
                                </span>
                                {product.isFeatured && (
                                    <span className="bg-yellow-100 text-yellow-600 px-2 py-1 rounded-full text-xs font-medium">
                                        Featured
                                    </span>
                                )}
                            </div>

                            <h1 className="text-3xl font-bold text-gray-900 mb-4">{product.name}</h1>

                            <div className="flex items-center space-x-4 mb-4">
                                <div className="flex items-center space-x-1">
                                    <Star className="w-5 h-5 text-yellow-500 fill-current" />
                                    <span className="text-lg font-semibold">
                                        {product.rating?.average?.toFixed(1) || '4.5'}
                                    </span>
                                    <span className="text-gray-600">
                                        ({product.rating?.count || 0} reviews)
                                    </span>
                                </div>
                            </div>

                            <div className="flex items-center space-x-4 mb-6">
                                <span className="text-3xl font-bold text-gray-900">₹{product.price}</span>
                                {product.originalPrice && product.originalPrice > product.price && (
                                    <span className="text-xl text-gray-500 line-through">
                                        ₹{product.originalPrice}
                                    </span>
                                )}
                            </div>
                        </div>

                        <div className="border-t border-b border-gray-200 py-6">
                            <h3 className="text-lg font-semibold text-gray-900 mb-4">Description</h3>
                            <p className="text-gray-600 leading-relaxed">{product.description}</p>
                        </div>

                        {/* Specifications */}
                        {product.specifications && (
                            <div>
                                <h3 className="text-lg font-semibold text-gray-900 mb-4">Specifications</h3>
                                <div className="grid grid-cols-2 gap-4">
                                    {Object.entries(product.specifications).map(([key, value]) => (
                                        <div key={key} className="flex justify-between">
                                            <span className="text-gray-600 capitalize">{key}:</span>
                                            <span className="text-gray-900">{value}</span>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}

                        {/* Quantity and Add to Cart */}
                        <div className="space-y-4">
                            <div className="flex items-center space-x-4">
                                <span className="text-gray-700">Quantity:</span>
                                <div className="flex items-center space-x-2">
                                    <button
                                        onClick={() => setQuantity(Math.max(1, quantity - 1))}
                                        className="w-8 h-8 rounded-full border border-gray-300 flex items-center justify-center hover:bg-gray-50"
                                    >
                                        <Minus className="w-4 h-4" />
                                    </button>
                                    <span className="w-12 text-center font-medium">{quantity}</span>
                                    <button
                                        onClick={() => setQuantity(quantity + 1)}
                                        className="w-8 h-8 rounded-full border border-gray-300 flex items-center justify-center hover:bg-gray-50"
                                    >
                                        <Plus className="w-4 h-4" />
                                    </button>
                                </div>
                            </div>

                            <div className="flex space-x-4">
                                <button
                                    onClick={handleAddToCart}
                                    disabled={isAddingToCart || product.stock === 0}
                                    className="flex-1 btn btn-primary py-3"
                                >
                                    {isAddingToCart ? (
                                        <div className="flex items-center justify-center">
                                            <div className="spinner mr-2"></div>
                                            Adding...
                                        </div>
                                    ) : (
                                        <>
                                            <ShoppingCart className="w-5 h-5 mr-2" />
                                            Add to Cart
                                        </>
                                    )}
                                </button>
                                <button className="btn btn-secondary">
                                    <Heart className="w-5 h-5" />
                                </button>
                                <button className="btn btn-secondary">
                                    <Share2 className="w-5 h-5" />
                                </button>
                            </div>

                            {product.stock === 0 && (
                                <p className="text-red-600 text-sm">Out of stock</p>
                            )}
                        </div>

                        {/* Shipping Info */}
                        <div className="bg-gray-50 rounded-lg p-4">
                            <h4 className="font-semibold text-gray-900 mb-2">Shipping Information</h4>
                            <div className="space-y-2 text-sm text-gray-600">
                                <div className="flex items-center space-x-2">
                                    <Truck className="w-4 h-4" />
                                    <span>
                                        {product.shipping?.available ? 'Available for shipping' : 'Local pickup only'}
                                    </span>
                                </div>
                                {product.shipping?.cost && (
                                    <div className="flex items-center space-x-2">
                                        <span>Shipping cost: ₹{product.shipping.cost}</span>
                                    </div>
                                )}
                                {product.shipping?.estimatedDays && (
                                    <div className="flex items-center space-x-2">
                                        <span>Estimated delivery: {product.shipping.estimatedDays} days</span>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                </div>

                {/* Vendor Info */}
                <div className="mt-12">
                    <div className="card p-6">
                        <h3 className="text-lg font-semibold text-gray-900 mb-4">Vendor Information</h3>
                        <div className="flex items-center space-x-4">
                            <div className="w-16 h-16 bg-gray-300 rounded-full flex items-center justify-center">
                                <User className="w-8 h-8 text-gray-600" />
                            </div>
                            <div className="flex-1">
                                <h4 className="font-semibold text-gray-900">{product.vendor?.name || 'Local Vendor'}</h4>
                                <div className="flex items-center space-x-4 text-sm text-gray-600">
                                    <div className="flex items-center space-x-1">
                                        <Star className="w-4 h-4 text-yellow-500 fill-current" />
                                        <span>{product.vendor?.rating || '4.5'}/5</span>
                                    </div>
                                    <div className="flex items-center space-x-1">
                                        <MapPin className="w-4 h-4" />
                                        <span>Jharkhand, India</span>
                                    </div>
                                </div>
                            </div>
                            <button className="btn btn-outline">
                                View Profile
                            </button>
                        </div>
                    </div>
                </div>

                {/* Reviews */}
                {product.reviews && product.reviews.length > 0 && (
                    <div className="mt-12">
                        <div className="card p-6">
                            <h3 className="text-lg font-semibold text-gray-900 mb-6">Customer Reviews</h3>
                            <div className="space-y-6">
                                {product.reviews.slice(0, 5).map((review, index) => (
                                    <div key={index} className="border-b border-gray-200 pb-6 last:border-b-0">
                                        <div className="flex items-center justify-between mb-2">
                                            <div className="flex items-center space-x-2">
                                                <div className="w-8 h-8 bg-gray-300 rounded-full flex items-center justify-center">
                                                    <span className="text-sm font-medium text-gray-600">
                                                        {review.user?.name?.charAt(0) || 'U'}
                                                    </span>
                                                </div>
                                                <span className="font-medium text-gray-900">
                                                    {review.user?.name || 'Anonymous'}
                                                </span>
                                            </div>
                                            <div className="flex items-center space-x-1">
                                                {[...Array(5)].map((_, i) => (
                                                    <Star
                                                        key={i}
                                                        className={`w-4 h-4 ${i < review.rating ? 'text-yellow-400 fill-current' : 'text-gray-300'
                                                            }`}
                                                    />
                                                ))}
                                            </div>
                                        </div>
                                        <p className="text-gray-600 mb-2">{review.comment}</p>
                                        <p className="text-sm text-gray-500">
                                            {new Date(review.createdAt).toLocaleDateString()}
                                        </p>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default ProductDetail;
