import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import {
    User,
    Mail,
    Phone,
    MapPin,
    Edit3,
    Save,
    X,
    Camera,
    Shield,
    Star
} from 'lucide-react';
import toast from 'react-hot-toast';

const Profile = () => {
    const { user, updateProfile } = useAuth();
    const [isEditing, setIsEditing] = useState(false);
    const [formData, setFormData] = useState({
        name: user?.name || '',
        email: user?.email || '',
        phone: user?.phone || '',
        role: user?.role || 'tourist',
        preferences: {
            language: user?.preferences?.language || 'en',
            interests: user?.preferences?.interests || [],
            budget: {
                min: user?.preferences?.budget?.min || 0,
                max: user?.preferences?.budget?.max || 0
            }
        }
    });

    const [loading, setLoading] = useState(false);

    const interestOptions = [
        'Nature & Wildlife',
        'Cultural Heritage',
        'Adventure Sports',
        'Religious Sites',
        'Local Cuisine',
        'Photography',
        'Shopping',
        'Relaxation'
    ];

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        if (name.startsWith('preferences.')) {
            const prefKey = name.split('.')[1];
            if (prefKey === 'budget.min' || prefKey === 'budget.max') {
                const budgetKey = prefKey.split('.')[1];
                setFormData(prev => ({
                    ...prev,
                    preferences: {
                        ...prev.preferences,
                        budget: {
                            ...prev.preferences.budget,
                            [budgetKey]: parseInt(value) || 0
                        }
                    }
                }));
            } else {
                setFormData(prev => ({
                    ...prev,
                    preferences: {
                        ...prev.preferences,
                        [prefKey]: value
                    }
                }));
            }
        } else {
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handleInterestToggle = (interest) => {
        setFormData(prev => ({
            ...prev,
            preferences: {
                ...prev.preferences,
                interests: prev.preferences.interests.includes(interest)
                    ? prev.preferences.interests.filter(i => i !== interest)
                    : [...prev.preferences.interests, interest]
            }
        }));
    };

    const handleSave = async () => {
        setLoading(true);
        try {
            const result = await updateProfile(formData);
            if (result.success) {
                setIsEditing(false);
            }
        } catch (error) {
            console.error('Update error:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleCancel = () => {
        setFormData({
            name: user?.name || '',
            email: user?.email || '',
            phone: user?.phone || '',
            role: user?.role || 'tourist',
            preferences: {
                language: user?.preferences?.language || 'en',
                interests: user?.preferences?.interests || [],
                budget: {
                    min: user?.preferences?.budget?.min || 0,
                    max: user?.preferences?.budget?.max || 0
                }
            }
        });
        setIsEditing(false);
    };

    return (
        <div className="min-h-screen bg-gray-50">
            {/* Header */}
            <div className="bg-gradient-to-r from-blue-600 to-purple-700 text-white py-16">
                <div className="container mx-auto px-4">
                    <h1 className="text-4xl font-bold mb-4">My Profile</h1>
                    <p className="text-xl text-blue-100">
                        Manage your account settings and preferences
                    </p>
                </div>
            </div>

            <div className="container mx-auto px-4 py-8">
                <div className="grid lg:grid-cols-3 gap-8">
                    {/* Profile Card */}
                    <div className="lg:col-span-1">
                        <div className="card p-6 text-center">
                            <div className="relative inline-block mb-4">
                                <div className="w-24 h-24 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full flex items-center justify-center mx-auto">
                                    <User className="w-12 h-12 text-white" />
                                </div>
                                {isEditing && (
                                    <button className="absolute bottom-0 right-0 bg-blue-600 text-white p-2 rounded-full hover:bg-blue-700 transition-colors">
                                        <Camera className="w-4 h-4" />
                                    </button>
                                )}
                            </div>

                            <h2 className="text-2xl font-bold text-gray-900 mb-2">
                                {user?.name || 'User Name'}
                            </h2>

                            <div className="flex items-center justify-center space-x-2 mb-4">
                                <span className="bg-blue-100 text-blue-600 px-3 py-1 rounded-full text-sm font-medium capitalize">
                                    {user?.role || 'tourist'}
                                </span>
                                {user?.isVerified && (
                                    <span className="bg-green-100 text-green-600 px-3 py-1 rounded-full text-sm font-medium flex items-center">
                                        <Shield className="w-3 h-3 mr-1" />
                                        Verified
                                    </span>
                                )}
                            </div>

                            <div className="flex items-center justify-center space-x-4 text-sm text-gray-500 mb-6">
                                <div className="flex items-center">
                                    <Star className="w-4 h-4 mr-1" />
                                    <span>{user?.rating || 0}/5</span>
                                </div>
                                <div className="flex items-center">
                                    <span>{user?.totalReviews || 0} reviews</span>
                                </div>
                            </div>

                            {!isEditing ? (
                                <button
                                    onClick={() => setIsEditing(true)}
                                    className="btn btn-primary w-full"
                                >
                                    <Edit3 className="w-4 h-4 mr-2" />
                                    Edit Profile
                                </button>
                            ) : (
                                <div className="flex space-x-2">
                                    <button
                                        onClick={handleSave}
                                        disabled={loading}
                                        className="btn btn-primary flex-1"
                                    >
                                        {loading ? (
                                            <div className="spinner mr-2"></div>
                                        ) : (
                                            <Save className="w-4 h-4 mr-2" />
                                        )}
                                        Save
                                    </button>
                                    <button
                                        onClick={handleCancel}
                                        className="btn btn-secondary flex-1"
                                    >
                                        <X className="w-4 h-4 mr-2" />
                                        Cancel
                                    </button>
                                </div>
                            )}
                        </div>
                    </div>

                    {/* Profile Details */}
                    <div className="lg:col-span-2 space-y-6">
                        {/* Basic Information */}
                        <div className="card p-6">
                            <h3 className="text-xl font-semibold text-gray-900 mb-6">Basic Information</h3>

                            <div className="grid md:grid-cols-2 gap-6">
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Full Name
                                    </label>
                                    {isEditing ? (
                                        <input
                                            type="text"
                                            name="name"
                                            value={formData.name}
                                            onChange={handleInputChange}
                                            className="input"
                                        />
                                    ) : (
                                        <div className="flex items-center space-x-2 p-3 bg-gray-50 rounded-lg">
                                            <User className="w-5 h-5 text-gray-400" />
                                            <span className="text-gray-900">{user?.name || 'Not provided'}</span>
                                        </div>
                                    )}
                                </div>

                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Email
                                    </label>
                                    <div className="flex items-center space-x-2 p-3 bg-gray-50 rounded-lg">
                                        <Mail className="w-5 h-5 text-gray-400" />
                                        <span className="text-gray-900">{user?.email}</span>
                                    </div>
                                    <p className="text-xs text-gray-500 mt-1">Email cannot be changed</p>
                                </div>

                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Phone Number
                                    </label>
                                    {isEditing ? (
                                        <input
                                            type="tel"
                                            name="phone"
                                            value={formData.phone}
                                            onChange={handleInputChange}
                                            className="input"
                                        />
                                    ) : (
                                        <div className="flex items-center space-x-2 p-3 bg-gray-50 rounded-lg">
                                            <Phone className="w-5 h-5 text-gray-400" />
                                            <span className="text-gray-900">{user?.phone || 'Not provided'}</span>
                                        </div>
                                    )}
                                </div>

                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Account Type
                                    </label>
                                    {isEditing ? (
                                        <select
                                            name="role"
                                            value={formData.role}
                                            onChange={handleInputChange}
                                            className="input"
                                        >
                                            <option value="tourist">Tourist</option>
                                            <option value="guide">Tour Guide</option>
                                            <option value="vendor">Local Vendor</option>
                                        </select>
                                    ) : (
                                        <div className="flex items-center space-x-2 p-3 bg-gray-50 rounded-lg">
                                            <Shield className="w-5 h-5 text-gray-400" />
                                            <span className="text-gray-900 capitalize">{user?.role || 'tourist'}</span>
                                        </div>
                                    )}
                                </div>
                            </div>
                        </div>

                        {/* Preferences */}
                        <div className="card p-6">
                            <h3 className="text-xl font-semibold text-gray-900 mb-6">Travel Preferences</h3>

                            <div className="space-y-6">
                                {/* Language */}
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Preferred Language
                                    </label>
                                    {isEditing ? (
                                        <select
                                            name="preferences.language"
                                            value={formData.preferences.language}
                                            onChange={handleInputChange}
                                            className="input"
                                        >
                                            <option value="en">English</option>
                                            <option value="hi">हिन्दी</option>
                                            <option value="bn">বাংলা</option>
                                            <option value="or">ଓଡ଼ିଆ</option>
                                        </select>
                                    ) : (
                                        <div className="p-3 bg-gray-50 rounded-lg">
                                            <span className="text-gray-900">
                                                {formData.preferences.language === 'en' ? 'English' :
                                                    formData.preferences.language === 'hi' ? 'हिन्दी' :
                                                        formData.preferences.language === 'bn' ? 'বাংলা' :
                                                            formData.preferences.language === 'or' ? 'ଓଡ଼ିଆ' : 'English'}
                                            </span>
                                        </div>
                                    )}
                                </div>

                                {/* Budget */}
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Budget Range (₹)
                                    </label>
                                    {isEditing ? (
                                        <div className="grid grid-cols-2 gap-4">
                                            <div>
                                                <input
                                                    type="number"
                                                    name="preferences.budget.min"
                                                    value={formData.preferences.budget.min}
                                                    onChange={handleInputChange}
                                                    placeholder="Min"
                                                    className="input"
                                                />
                                            </div>
                                            <div>
                                                <input
                                                    type="number"
                                                    name="preferences.budget.max"
                                                    value={formData.preferences.budget.max}
                                                    onChange={handleInputChange}
                                                    placeholder="Max"
                                                    className="input"
                                                />
                                            </div>
                                        </div>
                                    ) : (
                                        <div className="p-3 bg-gray-50 rounded-lg">
                                            <span className="text-gray-900">
                                                ₹{user?.preferences?.budget?.min || 0} - ₹{user?.preferences?.budget?.max || 0}
                                            </span>
                                        </div>
                                    )}
                                </div>

                                {/* Interests */}
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Interests
                                    </label>
                                    {isEditing ? (
                                        <div className="grid grid-cols-2 gap-2">
                                            {interestOptions.map(interest => (
                                                <label key={interest} className="flex items-center space-x-2">
                                                    <input
                                                        type="checkbox"
                                                        checked={formData.preferences.interests.includes(interest)}
                                                        onChange={() => handleInterestToggle(interest)}
                                                        className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                                                    />
                                                    <span className="text-sm text-gray-700">{interest}</span>
                                                </label>
                                            ))}
                                        </div>
                                    ) : (
                                        <div className="p-3 bg-gray-50 rounded-lg">
                                            {user?.preferences?.interests?.length > 0 ? (
                                                <div className="flex flex-wrap gap-2">
                                                    {user.preferences.interests.map((interest, index) => (
                                                        <span key={index} className="bg-blue-100 text-blue-600 px-2 py-1 rounded-full text-xs">
                                                            {interest}
                                                        </span>
                                                    ))}
                                                </div>
                                            ) : (
                                                <span className="text-gray-500">No interests selected</span>
                                            )}
                                        </div>
                                    )}
                                </div>
                            </div>
                        </div>

                        {/* Location */}
                        {user?.location && (
                            <div className="card p-6">
                                <h3 className="text-xl font-semibold text-gray-900 mb-6">Location</h3>
                                <div className="flex items-center space-x-2 p-3 bg-gray-50 rounded-lg">
                                    <MapPin className="w-5 h-5 text-gray-400" />
                                    <span className="text-gray-900">
                                        {user.location.coordinates[0]}, {user.location.coordinates[1]}
                                    </span>
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Profile;
