import React from 'react';
import { useParams, Link } from 'react-router-dom';
import { useQuery } from 'react-query';
import {
    MapPin,
    Star,
    Clock,
    DollarSign,
    Camera,
    Navigation,
    Heart,
    Share2,
    Phone,
    Globe
} from 'lucide-react';
import axios from 'axios';

const DestinationDetail = () => {
    const { id } = useParams();

    const { data: destination, isLoading } = useQuery(
        ['destination', id],
        () => axios.get(`/api/destinations/${id}`).then(res => res.data),
        {
            enabled: !!id,
        }
    );

    if (isLoading) {
        return (
            <div className="min-h-screen flex items-center justify-center">
                <div className="spinner"></div>
            </div>
        );
    }

    if (!destination) {
        return (
            <div className="min-h-screen flex items-center justify-center">
                <div className="text-center">
                    <h2 className="text-2xl font-bold text-gray-900 mb-4">Destination not found</h2>
                    <Link to="/destinations" className="btn btn-primary">
                        Back to Destinations
                    </Link>
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gray-50">
            {/* Hero Section */}
            <div className="relative h-96">
                <img
                    src={destination.images?.[0]?.url || "https://images.unsplash.com/photo-1506905925346-21bda4d32df4?ixlib=rb-4.0.3&auto=format&fit=crop&w=2070&q=80"}
                    alt={destination.name}
                    className="w-full h-full object-cover"
                />
                <div className="absolute inset-0 bg-black bg-opacity-40"></div>
                <div className="absolute bottom-8 left-8 text-white">
                    <h1 className="text-4xl font-bold mb-2">{destination.name}</h1>
                    <div className="flex items-center space-x-4">
                        <div className="flex items-center space-x-1">
                            <Star className="w-5 h-5 text-yellow-400 fill-current" />
                            <span className="text-lg">
                                {destination.rating?.average?.toFixed(1) || '4.5'}
                                ({destination.rating?.count || 0} reviews)
                            </span>
                        </div>
                        <span className="bg-blue-600 px-3 py-1 rounded-full text-sm font-medium capitalize">
                            {destination.category}
                        </span>
                    </div>
                </div>
            </div>

            <div className="container mx-auto px-4 py-8">
                <div className="grid lg:grid-cols-3 gap-8">
                    {/* Main Content */}
                    <div className="lg:col-span-2 space-y-8">
                        {/* Description */}
                        <div className="card p-6">
                            <h2 className="text-2xl font-bold text-gray-900 mb-4">About</h2>
                            <p className="text-gray-600 leading-relaxed">
                                {destination.description}
                            </p>
                        </div>

                        {/* Attractions */}
                        {destination.attractions && destination.attractions.length > 0 && (
                            <div className="card p-6">
                                <h2 className="text-2xl font-bold text-gray-900 mb-4">Attractions</h2>
                                <div className="grid md:grid-cols-2 gap-4">
                                    {destination.attractions.map((attraction, index) => (
                                        <div key={index} className="border border-gray-200 rounded-lg p-4">
                                            <h3 className="font-semibold text-gray-900 mb-2">{attraction.name}</h3>
                                            <p className="text-gray-600 text-sm">{attraction.description}</p>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}

                        {/* Activities */}
                        {destination.activities && destination.activities.length > 0 && (
                            <div className="card p-6">
                                <h2 className="text-2xl font-bold text-gray-900 mb-4">Activities</h2>
                                <div className="space-y-4">
                                    {destination.activities.map((activity, index) => (
                                        <div key={index} className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                                            <div>
                                                <h3 className="font-semibold text-gray-900">{activity.name}</h3>
                                                <p className="text-gray-600 text-sm">{activity.description}</p>
                                                <div className="flex items-center space-x-4 mt-2 text-sm text-gray-500">
                                                    <span className="flex items-center">
                                                        <Clock className="w-4 h-4 mr-1" />
                                                        {activity.duration}
                                                    </span>
                                                    <span className="bg-blue-100 text-blue-600 px-2 py-1 rounded-full text-xs">
                                                        {activity.difficulty}
                                                    </span>
                                                </div>
                                            </div>
                                            {activity.price && (
                                                <div className="text-right">
                                                    <span className="text-lg font-semibold text-gray-900">
                                                        ₹{activity.price}
                                                    </span>
                                                </div>
                                            )}
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}

                        {/* Reviews */}
                        {destination.reviews && destination.reviews.length > 0 && (
                            <div className="card p-6">
                                <h2 className="text-2xl font-bold text-gray-900 mb-4">Reviews</h2>
                                <div className="space-y-4">
                                    {destination.reviews.slice(0, 5).map((review, index) => (
                                        <div key={index} className="border-b border-gray-200 pb-4 last:border-b-0">
                                            <div className="flex items-center justify-between mb-2">
                                                <div className="flex items-center space-x-2">
                                                    <div className="w-8 h-8 bg-gray-300 rounded-full flex items-center justify-center">
                                                        <span className="text-sm font-medium text-gray-600">
                                                            {review.user?.name?.charAt(0) || 'U'}
                                                        </span>
                                                    </div>
                                                    <span className="font-medium text-gray-900">
                                                        {review.user?.name || 'Anonymous'}
                                                    </span>
                                                </div>
                                                <div className="flex items-center space-x-1">
                                                    {[...Array(5)].map((_, i) => (
                                                        <Star
                                                            key={i}
                                                            className={`w-4 h-4 ${i < review.rating ? 'text-yellow-400 fill-current' : 'text-gray-300'
                                                                }`}
                                                        />
                                                    ))}
                                                </div>
                                            </div>
                                            <p className="text-gray-600">{review.comment}</p>
                                            <p className="text-sm text-gray-500 mt-2">
                                                {new Date(review.createdAt).toLocaleDateString()}
                                            </p>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}
                    </div>

                    {/* Sidebar */}
                    <div className="space-y-6">
                        {/* Quick Info */}
                        <div className="card p-6">
                            <h3 className="text-lg font-semibold text-gray-900 mb-4">Quick Info</h3>
                            <div className="space-y-3">
                                <div className="flex items-center space-x-3">
                                    <MapPin className="w-5 h-5 text-gray-400" />
                                    <span className="text-gray-600">
                                        {destination.location?.address || destination.location?.district || 'Jharkhand'}
                                    </span>
                                </div>

                                {destination.timings && (
                                    <div className="flex items-center space-x-3">
                                        <Clock className="w-5 h-5 text-gray-400" />
                                        <span className="text-gray-600">
                                            {destination.timings.open} - {destination.timings.close}
                                        </span>
                                    </div>
                                )}

                                {destination.entryFee && (
                                    <div className="flex items-center space-x-3">
                                        <DollarSign className="w-5 h-5 text-gray-400" />
                                        <span className="text-gray-600">
                                            Adult: ₹{destination.entryFee.adult} | Child: ₹{destination.entryFee.child}
                                        </span>
                                    </div>
                                )}

                                {destination.bestTimeToVisit && destination.bestTimeToVisit.length > 0 && (
                                    <div className="flex items-start space-x-3">
                                        <Clock className="w-5 h-5 text-gray-400 mt-1" />
                                        <div>
                                            <span className="text-gray-600">Best time to visit:</span>
                                            <div className="flex flex-wrap gap-1 mt-1">
                                                {destination.bestTimeToVisit.map((time, index) => (
                                                    <span key={index} className="bg-blue-100 text-blue-600 px-2 py-1 rounded-full text-xs">
                                                        {time}
                                                    </span>
                                                ))}
                                            </div>
                                        </div>
                                    </div>
                                )}
                            </div>
                        </div>

                        {/* Actions */}
                        <div className="card p-6">
                            <div className="space-y-3">
                                <button className="w-full btn btn-primary">
                                    <Navigation className="w-4 h-4 mr-2" />
                                    Get Directions
                                </button>
                                <button className="w-full btn btn-secondary">
                                    <Heart className="w-4 h-4 mr-2" />
                                    Add to Favorites
                                </button>
                                <button className="w-full btn btn-outline">
                                    <Share2 className="w-4 h-4 mr-2" />
                                    Share
                                </button>
                            </div>
                        </div>

                        {/* Contact Info */}
                        <div className="card p-6">
                            <h3 className="text-lg font-semibold text-gray-900 mb-4">Contact</h3>
                            <div className="space-y-3">
                                <div className="flex items-center space-x-3">
                                    <Phone className="w-5 h-5 text-gray-400" />
                                    <span className="text-gray-600">+91 123 456 7890</span>
                                </div>
                                <div className="flex items-center space-x-3">
                                    <Globe className="w-5 h-5 text-gray-400" />
                                    <a href="#" className="text-blue-600 hover:text-blue-800">
                                        Visit Website
                                    </a>
                                </div>
                            </div>
                        </div>

                        {/* Nearby Destinations */}
                        {destination.nearbyDestinations && destination.nearbyDestinations.length > 0 && (
                            <div className="card p-6">
                                <h3 className="text-lg font-semibold text-gray-900 mb-4">Nearby Places</h3>
                                <div className="space-y-3">
                                    {destination.nearbyDestinations.map((nearby, index) => (
                                        <Link
                                            key={index}
                                            to={`/destinations/${nearby._id}`}
                                            className="flex items-center space-x-3 p-2 hover:bg-gray-50 rounded-lg transition-colors"
                                        >
                                            <img
                                                src={nearby.images?.[0]?.url || "https://images.unsplash.com/photo-1506905925346-21bda4d32df4?ixlib=rb-4.0.3&auto=format&fit=crop&w=100&q=80"}
                                                alt={nearby.name}
                                                className="w-12 h-12 object-cover rounded-lg"
                                            />
                                            <div>
                                                <h4 className="font-medium text-gray-900">{nearby.name}</h4>
                                                <p className="text-sm text-gray-500">2.5 km away</p>
                                            </div>
                                        </Link>
                                    ))}
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default DestinationDetail;
